import React, { Component } from 'react';
import './Home.css';
import About from './About';
import Intro from './Intro';
import Projects from './Projects';
import Animation from './Animation';
import Waypoint from 'react-waypoint';
import { CSSTransition } from 'react-transition-group';
import Plx from 'react-plx';



const parallaxData=[
    {
        start: 0,
        end: 200,
        properties: [
            {
                startValue: 'blue',
                endValue: 'green',
                property: 'backgroundColor'
            }
        ]
    },
];

class Home extends Component{
    constructor(props){
        super(props);
        this.state={
            loadPage: false,
        };
    }

    _LoadPage = () =>{
        this.setState({loadPage: true});
        console.log("in _showAnimation()");
    }

    render() {
        const { loadPage }  = this.state;
    return(
        <Waypoint
                onEnter={this._LoadPage.bind(this)}
                bottomOffset='0%'
            >
            <div className="Home">
                <div>
                    <CSSTransition
                        in={loadPage}
                        timeout={0}
                        classNames="page"
                    >
                        <div>

                            <Intro />
                            <About />
                            <Projects />
                            <Animation />

                        </div>

                    </CSSTransition>
                </div>
            </div>
            </Waypoint>

    );
    }
}
export default Home;
