import React, { Component } from 'react';
import './Home.css';
import About from './About';
import Intro from './Intro';
import Projects from './Projects';
import Animation from './Animation';
import Waypoint from 'react-waypoint';
import { CSSTransition } from 'react-transition-group';





class Home extends Component{
    constructor(props){
        super(props);
        this.state={
            isAnimation: false,
        };
    }

    _showAnimation = () =>{
        this.setState({isAnimation: true});
        console.log("in _showAnimation()");
    }

    render() {
        const { isAnimation }  = this.state;
    return(
        <div className="Home">
            <div  className="box">
                <Intro />
            </div>
            <About />
            <Projects />
            <Waypoint
                onEnter={this._showAnimation.bind(this)}
                bottomOffset='0%'
            >
                <div>
                    <CSSTransition
                        in={isAnimation}
                        timeout={0}
                        classNames="Anim-home"
                        unmountOnExit
                    >
                    <div>
                       <Animation>

                       </Animation>
                    </div>

                    </CSSTransition>
                </div>
            </Waypoint>


        </div>
    );
    }
}
export default Home;
